import React from "react";

const PetList = (props) => {
  console.log(props);
  // if (props.isVisible) {
  //   console.log(true);
  // } else {
  //   console.log(false);
  // }
  return (
    <ul>
      {props.pets.map((pet) => {
        return (
          <li key={pet.name}>
            <h3>
              {pet.name} - {pet.cuteness}
            </h3>
            {props.isVisible && <img src={pet.img} alt={pet.name} />}
          </li>
        );
      })}
    </ul>
  );
};

export default PetList;
